define({"topics" : [{"title":"Prerequisites","href":"datacollector\/UserGuide\/Destinations\/SQLServerBDCBulk.html#SQLServerBDCBulk_Prereq","attributes": {"data-id":"SQLServerBDCBulk_Prereq",},"menu": {"hasChildren":true,},"tocID":"SQLServerBDCBulk_Prereq-d46e143181","next":"SQLServerBDCBulk_Prereq-d46e143181",},{"title":"External Tables","shortdesc":"\n               <p class=\"shortdesc\"></p>\n            ","href":"datacollector\/UserGuide\/Destinations\/SQLServerBDCBulk.html#SQLServerBDCBulk-ExternalTables","attributes": {"data-id":"SQLServerBDCBulk-ExternalTables",},"menu": {"hasChildren":false,},"tocID":"SQLServerBDCBulk-ExternalTables-d46e143397","topics":[]},{"title":"Data Drift","shortdesc":"\n               <p class=\"shortdesc\">You can configure the SQL Server 2019 BDC Bulk Loader destination to automatically         handle changes to columns or tables,\n                  also known as data drift.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/SQLServerBDCBulk.html#SQLServerBDCBulk-DataDrift","attributes": {"data-id":"SQLServerBDCBulk-DataDrift",},"menu": {"hasChildren":true,},"tocID":"SQLServerBDCBulk-DataDrift-d46e143472","next":"SQLServerBDCBulk-DataDrift-d46e143472",},{"title":"Row Generation","shortdesc":"\n               <p class=\"shortdesc\">By default, the SQL Server 2019 BDC Bulk Loader destination includes all fields from         a record in the row written to\n                  the <span class=\"ph\">SQL Server 2019 BDC</span>         table. You can configure the destination to include only specified fields.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/SQLServerBDCBulk.html#SQLServerBDCBulk-RowGen","attributes": {"data-id":"SQLServerBDCBulk-RowGen",},"menu": {"hasChildren":false,},"tocID":"SQLServerBDCBulk-RowGen-d46e143870","topics":[]},{"title":"Configuring a SQL Server 2019 BDC Bulk Loader Destination","href":"datacollector\/UserGuide\/Destinations\/SQLServerBDCBulk.html#task_zkq_rfm_d3b","attributes": {"data-id":"task_zkq_rfm_d3b",},"menu": {"hasChildren":false,},"tocID":"task_zkq_rfm_d3b-d46e143999","topics":[]}]});